local PANEL = {}

function PANEL:DrawTriangleUp( cx, cy, w, h, color )

    local triangle = {
        { x = cx - w/2, y = cy + h/2 },
        { x = cx, y = cy - h/2 },
        { x = cx + w/2, y = cy + h/2 }
    }

    surface.SetDrawColor( color )
	draw.NoTexture()
	surface.DrawPoly( triangle )
end

function PANEL:DrawTriangleDown( cx, cy, w, h, color )

    local triangle = {
        { x = cx, y = cy + h/2 },
        { x = cx - w/2, y = cy - h/2 },
        { x = cx + w/2, y = cy - h/2 }
    }

    surface.SetDrawColor( color )
	draw.NoTexture()
