	surface.DrawRect(0,0, w ,h)
	local date = os.date( "%Y/%m/%d  %H:%M:%S", self.time + os.time())

	draw.SimpleText(date, "DermaDefault", w/2, 60 + (h - 60)/2, color_white, 1, 1 )
end

