        local pos = net.ReadVector()
        local data = net.ReadTable()
        local model = net.ReadString()
        local hp = net.ReadUInt(15)
        local shouldFall = net.ReadBool()
        local data_ = {}

        for k, v in pairs(data) do
            for i = 1, v do
                table.insert(data_, k)
            end
        end

        if shouldFall then
            local pod = ents.Create("summe_dispenser_flying")
            pod:SetPos(pos + Vector(0, 0, 600))
            pod:SetOwner(ply)
            pod.TargetPos = pos
            pod.NPCList = data_
            pod:Spawn()
            pod:SetModel(model)
            pod:SetMaxHealth(hp)
            pod:SetHealth(hp)

            local phys = pod:GetPhysicsObject()
            if IsValid(phys) then
                phys:Wake()
            end

            undo.Create("Droid Dispenser Pod")
                undo.AddEntity(pod)
                undo.SetPlayer(ply)
            undo.Finish()
        else
