            pod:SetPos(pos)
            pod.TargetPos = pos
            pod.NPCList = data_
            pod:Spawn()
            pod:SetModel(model)
            pod:SetMaxHealth(hp)
            pod:SetHealth(hp)

            undo.Create("Droid Dispenser Pod")
                undo.AddEntity(pod)
                undo.SetPlayer(ply)
            undo.Finish()
        end
    end)
end

