	effect:SetOrigin(tr.HitPos)
	effect:SetNormal(tr.HitNormal)
	effect:SetAngles(tr.HitNormal:Angle())
	effect:SetScale(1)
	effect:SetRadius(4)
	effect:SetMagnitude(1)
	effect:SetAttachment(1)
