	self:NetworkVar( "Int", 0, "WaitTime", { KeyName = "WaitTime",	Edit = { type = "Int", mix = 1, max = 500  } } ) 

	if SERVER then
		self:SetAnzeigeName("Unbekanntes Terminal")
