				Spread=Vector(0.005,0.005,0),
				Tracer=0,
				Force=50,
				Damage=55,
				Attacker=self.Shooter,
				Callback=function(attacker,trace,dmginfo) 
					--if CLIENT then
						
						local tracerEffect=EffectData()
