function chat.AddText(...)
	local args = {...}

	local t = {}
	for _, v in pairs (args) do
		if (isstring(v)) then
			table.insert(t, v)
		end
	end
	local origtext = table.concat(t, "")
	table.insert(args, {origtext = origtext})

	chat.OldAddText(...)
	LOUNGE_CHAT:AddToChatbox(LOUNGE_CHAT:ParseLineWrap(args, true))
end

