	local prevY = startY
	local prevPlyPanel = nil
	
	-- Prevent overlapping scroll items
	local _, selfY = self:GetPos()
	local selfW, selfH = self:GetSize()
	self:SetPos(0,selfY+panelOffset)
	self:SetSize(selfW,(selfH-panelOffset*2))
		
