    local function AddAttachmentButtons(pnl, txt)
        pnl:Clear()
        
        for attName, attTbl in SortedPairsByMemberValue(ArcCW.AttachmentTable, "PrintName") do
 
            local name = attTbl.PrintName 

            if attTbl.AbbrevName then
                name = attTbl.AbbrevName
            end

