            canMute = canmute,
            defMute = defmute,
            talk = talktbl,
            hear = heartbl
        }

        table.insert(AOCRP.Comlink.Channels, chanStruct)

end

    for k, v in SortedPairs(AOCRP.Config.Funks ) do
