		if LSCS.VERSION_GITHUB == 0 then
			surface.SetMaterial( icon_load_version )
			surface.SetDrawColor( Col )
			surface.DrawTexturedRectRotated( w - 14, h - 14, 16, 16, -CurTime() * 200 )

