				net.WriteDouble( checksum )
				net.WriteUInt( total_segments, 32 )
				net.WriteUInt( i, 32 )
				net.WriteUInt( #str, 32 )
				net.WriteData( str )       
			net.SendToServer()
		end )
    end

end

