function ENT:TestCollision( startpos, delta, isbox, extents, mask )
	local rad = self:GetRadius()
	local dist = startpos:DistToSqr(self:GetPos())
	if rad and dist < rad ^ 2 and bit.band( mask, CONTENTS_EMPTY ) == 0 then
		return false
	else
		return true
	end
end

