	ply.lscs_cmd[ IN_MOVERIGHT ] = cmd:KeyDown( IN_MOVERIGHT )
	ply.lscs_cmd[ IN_SPEED ] = cmd:KeyDown( IN_SPEED )
	ply.lscs_cmd[ IN_JUMP ] = cmd:KeyDown( IN_JUMP )
