local function lerpColor(t, a, b)
	local newCol = Color(255, 255, 255, 255)
	newCol.r = Lerp(t, a.r, b.r)
	newCol.g = Lerp(t, a.g, b.g)
	newCol.b = Lerp(t, a.b, b.b)
	newCol.a = Lerp(t, a.a, b.a)
	return newCol
end

