    if( BRICKS_SERVER.BASECLIENTCONFIG[key] ) then
        BRICKS_SERVER.CLIENTCONFIG = BRICKS_SERVER.CLIENTCONFIG or {}

        if( BRICKS_SERVER.BASECLIENTCONFIG[key][2] and BRICKS_SERVER.BASECLIENTCONFIG[key][2] == "bool" ) then
