function ENT:BurrowTo(pos)
	self:PlaySequenceAndMove("digin")
	if navmesh.IsLoaded() then
		pos = navmesh.GetNearestNavArea(pos):GetClosestPointOnArea(pos) or pos
	end
	self:SetPos(pos)
	self:DropToFloor()
	self:PlaySequenceAndMove("digout")
end
