	if IsValid(self) then
		for _, PoorSap in pairs(ents.FindInSphere(self:GetPos() +self:OBBCenter(),125)) do
			if IsValid(PoorSap) && (PoorSap:IsNPC() or PoorSap:IsNextBot()) && self:Disposition(PoorSap) == 1 then
