        elseif name == "rotate" then
            local ang = isangle(offsetTable.ang) and offsetTable.ang or Angle(0,0,0)

            slider.Slider:SetMin(-AAS.MaxAngleOffset)
            slider.Slider:SetMax(AAS.MaxAngleOffset)
