function MSD.AddModule(name, menu, icon)
	local mod = {
		name = name,
		icon = icon,
		menu = menu
	}

	local id = MSD.ModuleIds[name]

	if id then
		MSD.Modules[id] = mod
	else
		id = table.insert(MSD.Modules, mod)
		MSD.ModuleIds[name] = id
	end

	return id
end

