				self.MeleeAttackRange = 0
				self.ReachEnemyRange = 1000
				self.AvoidEnemyRange = 750
			end
		end
	end)
	if CLIENT then return end
	if self.UseWeapons then
		for i, class in ipairs(self.Weapons) do
			self:GiveWeapon(class)
		end
		self:RandomWeapon()
	end
end

