	self:SetLoop(data.loop)
	self:SetVolume(data.volume)

	ent:SetDupeURL(data.url, data.streamname, data.playstate ~= StreamRadioLib.STREAM_PLAYMODE_STOP)

	self.State.PlayMode = data.playstate
end

