end

local surface_SetDrawColor = surface.SetDrawColor
local rad = math.rad
local sin = math.sin
local cos = math.cos

function XeninUI:CalculateCircle(sx, sy, radius, vertexCount, angle)
    local vertices = {}
    local ang = -rad(angle or 0)
    local c = cos(ang)
    local s = sin(ang)
    for i = 0, 360, 360 / vertexCount do
        local radd = rad(i)
        local x = cos(radd)
        local y = sin(radd)

        local tempx = x * radius * c - y * radius * s + sx
        y = x * radius * s + y * radius * c + sy
        x = tempx

        vertices[#vertices + 1] = {
            x = x,
            y = y,
            u = u,
            v = v
        }
    end

    return vertices
end

function XeninUI:DrawCachedCircle(vertices, color)
    if vertices and #vertices > 0 then
        draw.NoTexture()
        surface_SetDrawColor(color)
        surface.DrawPoly(vertices)
    end
end

function XeninUI:DrawCircle(sx, sy, radius, vertexCount, color)
    local vertices = {}
    local ang = -rad(0)
    local c = cos(ang)
    local s = sin(ang)
    for i = 0, 360, 360 / vertexCount do
        local radd = rad(i)
        local x = cos(radd)
        local y = sin(radd)

        local tempx = x * radius * c - y * radius * s + sx
        y = x * radius * s + y * radius * c + sy
        x = tempx

        vertices[#vertices + 1] = {
            x = x,
            y = y,
            u = u,
            v = v
        }
    end

    if vertices and #vertices > 0 then
