}
function SWEP:RemoveEmptyRTCode()
	if not self.RTCode or type(self.RTCode) ~= "function" then return end

	local dump = string.dump(self.RTCode, true)
	for _, str in ipairs(EmptyFunctions) do
		if dump == str then
			self.RTCode = nil

			break
		end
	end
end
