		parent = (wpn.WModel or {})[1] or wpn
	end

	local muz = wpn:GetMuzzleDevice(wm)

	if !IsValid(muz) then
		muz = wpn
	end

	if !IsValid(muz) then
		self:Remove()
		return
	end

