    if not input.IsMouseDown( MOUSE_LEFT ) then return end

    local x, y = self:GetPos()
    local mx, my = input.GetCursorPos()
    local w, h = self:GetSize()

    local minsX = x
    local maxsX = x + w
    local minsY = y
    local maxsY = y + h
	
    if mx < minsX or mx > maxsX then
        return self:Remove()
    end

    if my < minsY or my > maxsY then
        return self:Remove()
    end
end
