		local easingsX = easings.outCubic( RealTime() - self.start, 0, self:GetWide(), 0.25 )
		local x, y = self:GetPos()
		local easeAmt = ( easingsX * -1 ) + self:GetWide()
		self:SetPos( easeAmt, y )
	else
		local x, y = self:GetPos()
		self.animating = false
	end
end
