                    return (value or 0)
                end
            end
            return 0
        end,
        formatValue = function(level)
            return string.Comma(level)
        end,
        customCheck = function()
            for _, data in ipairs(handlers) do
                if (_G[data.key]) then
                    return true
                end
            end

            return false
        end
    })
end

do
    local handlers = {
        {
            -- SAM
