function PANEL:AddCVar(strText, convar, on, off, funcFunction)
    local pnl = vgui.Create("PIXEL.MenuOptionCVar", self)
    pnl:SetMenu(self)
    pnl:SetText(strText)
    if funcFunction then pnl.DoClick = funcFunction end

    pnl:SetConVar(convar)
    pnl:SetValueOn(on)
    pnl:SetValueOff(off)

    self:AddPanel(pnl)

    return pnl
