        if !atttbl.Model then continue end

        local dupli = slottbl.DuplicateModels or {}

        local duplicheck = self:GetProcessedValue("Akimbo",true) or self:GetProcessedValue("DuplicateAttachments",true)

        for i = 0, #dupli do
