		self.r, self.g, self.b = Lerp(0.05, self.r, 0), Lerp(0.05, self.g, 0), Lerp(0.05, self.b, 0)
	end
	surface.SetDrawColor(self.r, self.g, self.b, 100)
	surface.DrawRect(0,0,w,h)
