        }

        function updateList(data, ostime = Date.now() / 1000) {
            const list = document.getElementById('list');
            list.innerHTML = '';

            data.forEach(entry => {
                const div = document.createElement('div');
                div.className = 'entry';

                const avatar = document.createElement('div');
