SWEP.PrintName="Phase Orb"SWEP.ViewModel="models/weapons/c_bugbait.mdl"SWEP.WorldModel="models/weapons/w_bugbait.mdl"SWEP.Description="An orb that allows you teleport to its destination by left clicking. Right clicking markates a destination to teleport to. Can only be purchased from sigils."SWEP.Base="weapon_zs_basethrown"DEFINE_BASECLASS("weapon_zs_basethrown")SWEP.Primary.Ammo="corruptedfragment"SWEP.MaxStock=3;SWEP.NextEmit=0;function SWEP:Holster()if CLIENT then self:SCKHolster()end;return true end;local a=Vector(0,0,72)local b=Vector(-17,-17,0)local c=Vector(17,17,4)SWEP.TeleportStatus="sigilteleport"SWEP.TeleportEffect="sigil_teleport"SWEP.IsPhaseOrb=true;function SWEP:PrimaryAttack()if CurTime()<self:GetNextPrimaryFire()then return end;local d=self:GetOwner()self:SetNextPrimaryFire(CurTime()+self.Primary.Delay)if SERVER then if d:GetStatus(self.TeleportStatus)then return end;if self:GetDTVector(10)==vector_origin then return end;local e=d:GiveStatus(self.TeleportStatus)if e:IsValid()then e:SetFromSigil(self)e:SetEndTime(CurTime()+3.5*d:GetSigilTeleportMul(true))e.TPDuration=3.5;e.PostTeleport=function()self:TakePrimaryAmmo(1)self.NextDeploy=CurTime()+1;local f={}local g={mask=CONTENTS_EMPTY,mins=Vector(-16,-16,0),maxs=Vector(16,16,72)}g.start=self:GetDTVector(10)g.endpos=g.start;g.filter=function(h)if h:IsValidLivingZombie()then f[#f+1]=h end end;util.TraceHull(g)for i,j in pairs(f)do d:TakeSpecialDamage(1000,DMG_DISSOLVE,d)end end end end end;function SWEP:SecondaryAttack()if not self:CanPrimaryAttack()then return end;self:SetNextPrimaryFire(CurTime()+1.5)local d=self:GetOwner()local k=d:GetViewModel()if IsValid(k)then k:SendViewModelMatchingSequence(k:LookupSequence("squeeze")or 0)end;d:DoAttackEvent()if CLIENT then return end;local l=d:GetPos()+Vector(0,0,1)local m=player.GetAll()local n=util.TraceHull({start=l,endpos=l+a,mins=b,maxs=c,mask=MASK_SOLID_BRUSHONLY,filter=m}).Hit;if n then d:CenterNotify(COLOR_RED,translate.Get("phase_orb_not_enough_room"))return end;local o=util.TraceHull({start=l,endpos=l-a,mins=b,maxs=c,mask=MASK_SOLID_BRUSHONLY})if o.HitSky or o.HitNoDraw then d:CenterNotify(COLOR_RED,translate.Get("phase_orb_not_enough_room"))return end;for i,p in ipairs(GAMEMODE:GetSigils())do if p:GetPos():DistToSqr(d:GetPos())<=225000 then d:CenterNotify(COLOR_RED,translate.Get("phase_orb_too_near_sigil"))return end end;for i,p in ipairs(GAMEMODE:GetSupplyCrates())do if p:GetPos():DistToSqr(d:GetPos())<=225000 then d:CenterNotify(COLOR_RED,translate.Get("phase_orb_too_near_supplycrate"))return end end;for i,p in ipairs(ents.FindByClass("prop_obj_exitpoint"))do if p:GetPos():DistToSqr(d:GetPos())<=225000 then d:CenterNotify(COLOR_RED,translate.Get("phase_orb_too_near_exitpoint"))return end end;self:SetDTVector(10,d:GetPos())d:CenterNotify(COLOR_GREEN,"You've marked this location to teleport to.")d:SendLua("MySelf:EmitSound(\"buttons/button1.wav\", 50, 35, 0.5)")end;local q=Material("zombiesurvival/agility.png")function SWEP:DrawHUD()BaseClass.DrawHUD(self)surface.SetMaterial(q)local r=self:GetDTVector(10)if r~=vector_origin then r.z=r.z+32;local s=r:ToScreen()if s.visible then local t,u=s.x,s.y;local v=64;local w,x,y=140,100,210;surface.SetDrawColor(w,x,y,150)surface.DrawTexturedRect(t-v*0.5,u-v*0.5,v,v)if CurTime()>=self.NextEmit then self.NextEmit=CurTime()+0.05;local z=ParticleEmitter(r)if not z then return end;z:SetNearClip(16,24)local A=z:Add("effects/yellowflare",r)A:SetVelocity(VectorRand():GetNormalized()*math.Rand(155,155))A:SetDieTime(math.Rand(0.3,0.4))A:SetStartAlpha(math.Rand(200,220))A:SetEndAlpha(0)A:SetStartSize(33)A:SetEndSize(0)A:SetRoll(math.Rand(-0.2,0.2))A:SetColor(w,x,y)z:Finish()z=nil;collectgarbage("step",64)end end end end
