;g.Gravity=0 end;if IsFirstTimePredicted()then if self:GetVariant()==2 or self:GetVariant()==3 then local k=EffectData()k:SetOrigin(d:GetShootPos())k:SetNormal(d:GetAimVector())EffectTransmitFilter("explosion_cold",k,nil,d)else local k=EffectData()k:SetOrigin(d:WorldSpaceCenter())k:SetNormal(d:GetAimVector())EffectTransmitFilter("explosion_wispball",k,nil,d)end end end end;local function l(d,m,n)local o=d:EyePos()local p=d:GetAimVector()local q=m or 128;local r=d:GetFOV()/2;local s={}if n then for t,g in ipairs(team.GetPlayers(TEAM_HUMAN))do if d==g then continue end;if not g:IsValidLivingHuman()then continue end;if g:GetStatus("wisp_tether")then continue end;local u=g:EyePos()if u:Distance(o)>q then continue end;local v=u-o;local w=p:Dot(v)/v:Length()local x=math.acos(w)local y=math.deg(x)if y<r then local z=g:NearestPoint(o)if WorldVisible(o,z,CONTENTS_SOLID)then s[#s+1]={g,y}end end end else for t,g in ipairs(team.GetPlayers(TEAM_UNDEAD))do if d==g then continue end;if not g:IsValidLivingZombie()then continue end;if g:GetStatus("wisp_tether")then continue end;if g:GetBossTier()>BOSSTIER_BOSS then continue end;local u=g:EyePos()if u:Distance(o)>q then continue end;local v=u-o;local w=p:Dot(v)/v:Length()local x=math.acos(w)local y=math.deg(x)if y<r then local z=g:NearestPoint(o)if WorldVisible(o,z,CONTENTS_SOLID)then if g:GetBossTier()<BOSSTIER_DEMIBOSS then y=y*2 elseif g:GetBossTier()>BOSSTIER_DEMIBOSS then y=y*0.66 end;s[#s+1]={g,y}end end end end;table.sort(s,function(A,B)return A[2]<B[2]end)return s end;function SWEP:SecondaryAttack()if CLIENT then return end;if CurTime()<self:GetNextTether()or(self.NextTry or 0)>CurTime()then return end;if self.TetherStatus and self.TetherStatus:IsValid()then self.TetherStatus:Remove()self.TetherStatus=nil;return end;local d=self:GetOwner()local e=d:HasSkillMod("wisp_tether_human")local C=false;if e then local n=l(d,self.TetherReach*3,true)local D=n[1]if D then C=D[1]end else local E=l(d,self.TetherReach*3)local F=E[1]if F then C=F[1]end end;if not C then self.NextTry=CurTime()+0.2;return end;local G=C:GiveStatus("wisp_tether")if G and G:IsValid()then G:SetApplier(d)G:SetReach(self.TetherReach)G.Human=e end;self.TetherStatus=G;self:SetTether(C)self:SetNextTether(CurTime()+self.TetherCooldown*d:GetAbilitySpeedMul())local H=C:WorldSpaceCenter()local I=d:WorldSpaceCenter()local J=H:Distance(I)if J>self.TetherReach*0.9 then d:SetVelocity((H-I):GetNormalized()*J*3.5)end;local k=EffectData()k:SetOrigin(C:WorldSpaceCenter())k:SetNormal(C:GetAimVector())EffectTransmitFilter("explosion_wisptether",k,true,d)end;function SWEP:Reload()local d=self:GetOwner()if CurTime()>=self:GetNextSecondaryAttack()then self:SetNextSecondaryAttack(CurTime()+5)if self:GetVariant()==2 or self:GetVariant()==3 then self:EmitSound("ambient/wind/wind_moan"..math.random(2)..".wav")else self:EmitSound("npc/scanner/scanner_talk2.wav")end end end;function SWEP:ShouldDisplayMedicalAura()return true end
