(215,170,50,255)end end;a.BuffInspecting="buff_agility"end).CollectiveName="Agility"local function f(g,h)if not(g:IsValidLivingHuman()and h:IsValidLivingHuman())then return false end;return g:GetScore()>h:GetScore()end;SWEP.NextTargetPick=0;function SWEP:Think()if CLIENT then local i=self:GetOwner()local j=input.GetKeyCode(input.LookupBinding("+menu",true)or"q")if self.NextTargetPick>CurTime()or i~=MySelf or not input.IsButtonDown(j)then return end;local k=self:GetPlayersInMedicalView(self.SelectionRange*i:GetModVal("medical_tool_range")/2,true)table.sort(k,f)self.CurPlayerPos=(self.CurPlayerPos or 0)+1;if self.CurPlayerPos>10 or self.CurPlayerPos>#k then self.CurPlayerPos=1 end;self.NextTargetPick=CurTime()+self:GetFireDelay()+0.05;local l=k[self.CurPlayerPos]and k[self.CurPlayerPos]or NULL;i:ConCommand("zstoolplayerlockon "..(l and l:EntIndex()or""))self:SetNextClientThink(CurTime()+0.1)end;return BaseClass.Think(self)end;function SWEP:GetFireDelay(m)local i=self:GetOwner()return BaseClass.GetFireDelay(self,m)*i:GetModVal("med_gun_fire_delay")end;function SWEP:GetReloadSpeedMultiplier()local i=self:GetOwner()return BaseClass.GetReloadSpeedMultiplier(self)*i:GetModVal("med_gun_reload_speed")end;function SWEP:SetSeekedPlayer(b)self:SetDTEntity(6,b)end;function SWEP:GetSeekedPlayer()return self:GetDTEntity(6)end;function SWEP:CanSecondaryAttack()if self:GetOwner():IsHolding()then return false end;return self:GetNextSecondaryFire()<=CurTime()end;function SWEP:SecondaryAttack()if not self:CanSecondaryAttack()then return end;self:SetNextSecondaryFire(CurTime()+0.4)if CLIENT and IsFirstTimePredicted()then local i=self:GetOwner()local l=i:CompensatedMeleeTrace(self.SelectionRange*i:GetModVal("medical_tool_range"),2.5,nil,nil,true).Entity;i:ConCommand("zstoolplayerlockon "..(l and l:EntIndex()or""))end end;function SWEP:OnUpgrade(n)local o=n:GetSeekedPlayer()if o and o:IsValidLivingHuman()then self:SetSeekedPlayer(o)end end;function SWEP:DoRefund()local i=self:GetOwner()lo