SWEP.PrintName="'Shaper' Repair Welder"SWEP.Description="A crude welding tool which uses pulse ammo and doubles as a short range weapon. Can highlight weakpoints and ideal welding points with secondary fire, consumes all heat. The tool has two fire modes, 'Repair' and 'Weld'. 'Repair' mode repairs props, while 'Weld' mode can be used when two props are nailed together, siphoning repair health from one prop to the other and prop health once no repair health is available. If you restore over 200% of a prop's health, you lose 75% of welding strength."SWEP.Base="weapon_zs_base"DEFINE_BASECLASS("weapon_zs_base")SWEP.HoldType="pistol1h"SWEP.IronSightsHoldType="revolver"SWEP.ViewModel="models/weapons/c_pistol.mdl"SWEP.WorldModel="models/weapons/w_pist_deagle.mdl"SWEP.UseHands=true;SWEP.ShowViewModel=false;SWEP.ShowWorldModel=false;SWEP.Primary.Delay=0.1;SWEP.Primary.MaxDistance=110;SWEP.Primary.Damage=28;SWEP.Primary.HullSize=3;SWEP.MaxWeldSpots=4;SWEP.HeadshotMulti=1.25;SWEP.Primary.ClipSize=30;SWEP.Primary.Ammo="pulse"GAMEMODE:SetupDefaultClip(SWEP.Primary)SWEP.ConeMax=0;SWEP.ConeMin=0;SWEP.Tier=4;SWEP.MaxStock=3;SWEP.WalkSpeed=SPEED_NORMAL;SWEP.FireAnimSpeed=0.03;SWEP.FireSoundPitch=33;SWEP.TracerName="tracer_welder"SWEP.HealStrength=1;SWEP.UseCombinedAmmo=true;SWEP.RepairTool=true;SWEP.EssenceType=ESSENCE_CADER;SWEP.ScrapScalingMul=0.9;SWEP.AltMax=3;SWEP.AltMaxSave=SWEP.AltMax;SWEP.Primary.DelaySave=SWEP.Primary.Delay;SWEP.SkillRecoilScale=0;SWEP.LastCharge=0;SWEP.HeatChargeDelay=0.1;SWEP.PrimaryAttackAnim=ACT_VM_IDLE;SWEP.HideStat={HeadshotMulti=true,ClipSize=true,ConeMin=true,ConeMax=true}SWEP.WeaponBuildup={Colour={50,180,255,255},Name="Charge",NameFull="Charge",Requirement=1}GAMEMODE:AttachWeaponModifier(WEAPON_MODIFIER_MAX_DISTANCE,SWEP.Primary.MaxDistance*0.05,1)GAMEMODE:AttachCustomWeaponModifier("HealStrength","Repair Amount",0.08,1)function SWEP:Initialize()self.FiringSound=CreateSound(self,"^thrusters/rocket00.wav")self.FiringSound:SetSoundLevel(65)BaseClass.Initialize(self)self:AddFireMode("Repair","REPAIR","buttons/lever5.wav",function()self.Primary.Delay=self.Primary.DelaySave;self.AltMax=3 end)self:AddFireMode("Weld","WELD",")ambient/fire/gascan_ignite1.wav",function()self.Primary.Delay=self.Primary.DelaySave;self.AltMax=self.AltMaxSave end)self:SetWeaponBuildup(1)end;function SWEP:PrimaryAttack()if not self:CanPrimaryAttack()then return end;self:CalculateNextAttackTime(self:GetFireDelay())local a=self:GetWeaponBuildup()if a<=0 then self:Reload()return end;local b=self:GetAltUsage()if b>=self.AltMax then self:TakeAmmo()b=0 end;self:SetAltUsage(b+1)self:ShootBullets(self.Primary.Damage,self.Primary.NumShots,self:GetCone())self.IdleAnimation=CurTime()+self:SequenceDuration()self.LastCharge=CurTime()+0.5;if SERVER then self:SetWeaponBuildup(math.max(a-0.025,0))end;if self:GetGunState()~=1 and IsFirstTimePredicted()then self:EmitStartFiringSound()self:SetGunState(1)end end;function SWEP:Think()BaseClass.Think(self)if self:GetGunState()==1 then self.FiringSound:PlayEx(1,self.FireSoundPitch+CurTime()%1)if CurTime()>=self:GetNextPrimaryFire()+0.1 then self:SetGunState(0)self:SetNextPrimaryFire(CurTime()+0.01)self:EmitSound("ambient/energy/weld2.wav",75,155)self.FiringSound:Stop()end end;if SERVER then self:ManageHeat()end end;function SWEP:Holster()local c=self:GetOwner()if c:IsValid()then c.WelderInactiveTime=CurTime()end;return BaseClass.Holster(self)end;local d=0.04;function SWEP:Deploy()local e=6;local c=self:GetOwner()if c.WelderInactiveTime then local f=c.WelderInactiveTime and CurTime()-c.WelderInactiveTime or 0;self:AddWeaponBuildup(f*e*d)end;return BaseClass.Deploy(self)end;function SWEP:ManageHeat()local c=self:GetOwner()if CurTime()>=self:GetNextPrimaryFire()+0.05 then self:SetHeatState(2)end;if c and c:IsValid()then if self:GetHeatState()==1 then local a=self:GetWeaponBuildup()if a==0 then self:SetHeatState(2)self:EmitSound("ambient/machines/thumper_startup1.wav",60,80)end elseif self:GetHeatState()==2 and self:GetNextPrimaryFire()<CurTime()-0.05 then local g=CurTime()if self.LastCharge+self.HeatChargeDelay<g then self:AddWeaponBuildup(d)self.LastCharge=g end;if self:GetWeaponBuildup()==1 then self:SetHeatState(0)self:EmitSound("ambient/machines/thumper_top.wav",65,60)end end end end;function SWEP:Reload()if self:GetHeatState()==0 and self:GetWeaponBuildup()~=1 then self:SetHeatState(2)self:EmitSound("ambient/machines/thumper_startup1.wav",60,80)end end;function SWEP:CanPrimaryAttack()if self:GetPrimaryAmmoCount()<=0 then return false end;if self:GetOwner():IsHolding()or self:GetOwner():GetBarricadeGhosting()or self:GetReloadFinish()>0 then return false end;return self:GetNextPrimaryFire()<=CurTime()end;function SWEP:OnPoppedRepairPoint(h,i,j)local c=self:GetOwner()if self:GetFireMode()==0 then h:RepairPropWithTool(c,self,i,j)else h:WeldPropWithTool(c,self,i,j*c:GetModVal("repair_rate"))end end;local k=function(l)return l:IsPhysicsProp()and l:IsNailed()and l:GetBarricadeRepairs()>0 end;local m={mins=Vector(-2,-2,-2),maxs=Vector(2,2,2),mask=MASK_SOLID,filter=k}function SWEP:SecondaryAttack()if not SERVER or self:GetWeaponBuildup()<self.WeaponBuildup.Requirement then return end;local c=self:GetOwner()local n=c:GetShootPos()local o=n+c:GetAimVector()*self.Primary.MaxDistance;m.start=n;m.endpos=o;local i=util.TraceHull(m)local l=i.Entity;if IsValid(l)then self:SetWeaponBuildup(0)l:EmitSound("ambient/energy/weld1.wav",70,60,0.7,CHAN_ITEM)self.LastCharge=CurTime()+1;local p=l:OBBMins()local q=l:OBBMaxs()local r,s,t,u,v,w=p.x,p.y,p.z,q.x,q.y,q.z;local x=l:GetBarricadeHealth()local y=l:GetMaxBarricadeHealth()local z=i.HitPos;local A,B,C=z.x,z.y,z.z;local D=self.MaxWeldSpots;for E=1,D do local F=Vector(math.Clamp(A+math.Rand(-8*E,8*E),A+r,A+u),math.Clamp(B+math.Rand(-8*E,8*E),B+s,B+v),math.Clamp(C+math.Rand(-8*E,8*E),C+t,C+w))l:CreateRepairPointStatus(c,F,true)end end end;function SWEP.BulletCallback(G,i,H)if i.HitWorld then util.Decal("FadingScorch",i.HitPos+i.HitNormal,i.HitPos-i.HitNormal)end;local I=H:GetInflictor()local J,K=I:TryHitRepairPoint(I.Primary.MaxDistance*1.25)if SERVER and IsValid(J)and J.Hit and J:Welded()then J.WeldHits=(J.WeldHits or 0)+1;J:Hit(K,I,I.HealStrength*GAMEMODE.NailHealthPerRepair,J.WeldHits,G)elseif SERVER and i.Entity then if i.Entity:IsNailed()then if I:GetFireMode()==0 then I:TryRepairProp(i.Entity,i)else i.Entity:WeldPropWithTool(G,I,i,I.HealStrength*GAMEMODE.NailHealthPerRepair*G:GetModVal("repair_rate"))end elseif i.Entity:IsValidLivingZombie()and math.random(6)==1 then i.Entity:AddBurnDamage(H:GetDamage(),G,I)end end;return{impact=false}end;function SWEP:CanReload()return false end;function SWEP:EmitStartFiringSound()self:EmitSound("ambient/energy/weld1.wav",75,230)end;function SWEP:SetGunState(L)self:SetDTInt(1,L)end;function SWEP:GetGunState(L)return self:GetDTInt(1)end;function SWEP:SetAltUsage(M)self:SetDTInt(2,M)end;function SWEP:GetAltUsage()return self:GetDTInt(2)end;function SWEP:SetHeatState(L)self:SetDTInt(3,L)end;function SWEP:GetHeatState(L)return self:GetDTInt(3)end
