AddCSLuaFile()

ENT.RenderGroup = RENDERGROUP_BOTH
ENT.Type = 'anim'
ENT.Base = 'base_anim'

ENT.PrintName = 'Cannon Blast'
ENT.Author = 'Liam0102'
ENT.Category = 'Star Wars'
ENT.Spawnable = false;
ENT.AdminSpawnable = false

ENT.Damage = 600

local purple = Color(255, 255, 255, 1)

if SERVER then
	function ENT:SetAttacker( ent ) self._attacker = ent end

	function ENT:Initialize()
		self:SetModel 'models/props_junk/PopCan01a.mdl'
		self:SetSolid(SOLID_VPHYSICS)
		self:SetMoveType(MOVETYPE_VPHYSICS)
		self:PhysicsInit(SOLID_VPHYSICS)
		self:StartMotionController()
		self:SetUseType(SIMPLE_USE)
		self:SetRenderMode(RENDERMODE_TRANSALPHA)
		self:SetColor(purple)
	end
 
	function ENT:Prepare(e, vel, ang)
		local phys = self:GetPhysicsObject()
		phys:SetMass(50)
		phys:EnableGravity(true)

		if not ang then
			ang = e:GetForward()
		end

		phys:EnableMotion(true)
		phys:SetVelocity(ang * (3000 * vel))
	end

    function ENT:PhysicsCollide(data, physobj)
        for i = 1, math.Round(self.Damage / 100) do
            local pos = self:GetPos() + self:GetForward() * math.random(-self.Damage / 2, self.Damage / 2) + self:GetRight() * math.random(-self.Damage / 2, self.Damage / 2)
            local fx = EffectData()
            fx:SetOrigin(pos)

            util.Effect('Explosion', fx, true, true)
        end

        for k, v in pairs(ents.FindInSphere(self:GetPos(), self.Damage)) do
            local dist = (self:GetPos() - v:GetPos()):Length()
            local dmg = math.Clamp((self.Damage or 600) - dist, 0, (self.Damage or 600))

            v:TakeDamage(dmg)
        end

        timer.Simple(0, function()
            if IsValid(self) then
                self:Remove()
            end
        end)
    end
	
end

