local oop = medialib.load("oop")

local SoundcloudService = oop.class("SoundcloudService", "HTMLService")
SoundcloudService.identifier = "soundcloud"

local all_patterns = {
	"^https?://www.soundcloud.com/([A-Za-z0-9_%-]+/[A-Za-z0-9_%-]+)/?.*$",
	"^https?://soundcloud.com/([A-Za-z0-9_%-]+/[A-Za-z0-9_%-]+)/?.*$",
}

function SoundcloudService:parseUrl(url)
	for _, pattern in pairs(all_patterns) do
		local path = string.match(url, pattern)
		if path then
			return {id = path}
		end
	end
end

function SoundcloudService:isValidUrl(url)
	return self:parseUrl(url) ~= nil
end

local player_url = "https://gmod-api.superiorservers.co/static/gmod-medialib/soundcloud.html?id="
function SoundcloudService:resolveUrl(url, callback)
	local urlData = self:parseUrl(url)
	local urlEncoded = urlData.id:URLEncode()
	callback(player_url .. urlEncoded, {})
end

function SoundcloudService:directQuery(url, callback)
	local urlData = self:parseUrl(url)
	if not urlData then
		return callback("Invalid URL")
	end

	local url = "https://soundcloud.com/oembed?format=json&url=https://soundcloud.com/" .. urlData.id

	http.Fetch(url, function(result, size)
		if size == 0 then
			return
		end

		local entry = util.JSONToTable(result)
		if not entry or not entry.title or not entry.type then
			return
		end

		local response = self:BuildResponse(entry, urlData)
		response.raw = entry

		callback(nil, response)
	end, function(err) callback("HTTP: " .. err) end)
end

function SoundcloudService:BuildResponse(item, urlData)
	local id = urlData.id

	return {
		id = id,
		title = item.title,
		icon = item.thumbnail_url,
		duration = -1
	}
end

return SoundcloudService

