/halloween/cinematic-jump-scare-hit.wav'}srp.dialogue.Register({id='HALLOWEENTROLL',name='Mr. Troll',icon=Material('galactic/skills/melee.png'),color=Color(128,0,32),CanUse=function()return true end,tree={{speech='Stop staring at me and help me! Give me some delicious pumpkins so I can survive and I\'ll give\nyou a treat.',options={{text='I want to help you. Take some of my pumpkins!',close=true,OnClick=function(self,a,i)if a.HalloweenNextTrick&&a.HalloweenNextTrick>CurTime()then return end;if SERVER then else a:EmitSound(h[math.random(1,#h)])end;a.HalloweenNextTrick=CurTime()+5 end},{text='I would like to offer you something else.',OnClick=function(self,a)if CLIENT then srp.ui.Create('srp_spooky',function(d)d:Populate(0)end)end end},{text='Fuck off!',close=true}}}}})srp.dialogue.Register({id='STORE_SUMMERFOOD',name='Summer Store',icon=Material('galactic/skills/melee.png'),color=col.purple:Copy(),CanUse=function()return true end,tree={{speech='Refreshing drinks and BBQ foods',options={{text='Show me the goods',OnClick=function(self,a)return true end},{text='No thankyou.',close=true}}}}})srp.dialogue.Register({id='STORE_HALLOWEENFOOD',name='Halloween Store',icon=Material('galactic/skills/melee.png'),color=col.purple:Copy(),CanUse=function()return true end,tree={{speech='I have tasty snacks for you. Why not collect me some pumpkins and we can trade?',options={{text='Show me the snacks',OnClick=function(self,a)return true end},{text='No thankyou.',close=true}}}}})srp.dialogue.Register({id='STORE_CONSTRUCTION',name='Eyekeya',icon=Material('galactic/skills/melee.png'),color=col.yellow:Copy(),CanUse=function()return true end,tree={{speech='The easiest way to create your home. Do you want to see what we have to offer?',options={{text='Show me the snacks',OnClick=function(self,a)return true end},{text='No thankyou.',close=true}}}}})srp.dialogue.Register({id='FOODSTORE',name='Food Stores',icon=Material('galactic/ui/coffee.png'),color=Color(128,0,32),CanUse=function()return t