AddCSLuaFile()DEFINE_BASECLASS("base_wire_entity")ENT.PrintName="Wire Vehicle Exit Point"if CLIENT then return end;function ENT:Initialize()BaseClass.Initialize(self)self.Inputs=WireLib.CreateInputs(self,{"Entity (Links the exit point controller to a single specified vehicle and unlinks all others.) [ENTITY]","Entities (Links the exit point controller to the specified array of vehicles.\nEntities will be linked one by one and this value can't be changed during this time.) [ARRAY]","Position (Whenever a player exits a linked vehicle, they will be teleported to this position.\nOnly either this or 'Local Position' can be used at a time. The last changed input is used.) [VECTOR]","Local Position (Whenever a player exits a linked vehicle, they will be teleported to this position, relative to the vehicle they just exited.\nOnly either this or 'Position' can be used at a time. The last changed input is used.) [VECTOR]","Angle (Whenever a player exits a linked vehicle, they will be rotated to face this angle.\nOnly either this or 'Local Angle' can be used at a time. The last changed input is used.) [ANGLE]","Local Angle (Whenever a player exits a linked vehicle, they will rotated to face this angle, relative to the vehicle they just exited.\nOnly either this or 'Angle' can be used at a time. The last changed input is used.) [ANGLE]"})self.Position=Vector(0,0,0)self.Angle=Angle(0,0,0)self.Entities={}self.Global=false;self.GlobalAngle=false;self:AddExitPoint()self:ShowOutput()end;function ENT:TriggerInput(a,b)if a=="Entity"then self.Entities={}if self:CheckPP(b)then self:LinkEnt(b)end elseif a=="Entities"then if self.ToLink then return end;self:ClearEntities()if next(b)!=nil then self.ToLink=table.Copy(b)self.ToLinkCounter=nil end elseif a=="Position"then self.Position=b;self.Global=true elseif a=="Local Position"then self.Position=b;self.Global=false elseif a=="Angle"then self.Angle=b;self.GlobalAngle=true elseif a=="Local Angle"then self.Angle=b;self.GlobalAngle=false end;self:ShowOutput()end;function ENT:ShowOutput()self:SetOverlayText(string.format("Entities linked: %i\n%sPosition: (%.2f, %.2f, %.2f)%s",table.Count(self.Entities),self.Global&&""||"Local ",self.Position.x,self.Position.y,self.Position.z,self.ToLink&&self.ToLinkCounter&&"\nLinking "..#self.ToLink-self.ToLinkCounter.." entities..."||""))end;function ENT:CheckPP(c)return IsValid(c)&&WireLib.CanTool(self:GetPlayer(),c,"wire_hoverdrivecontroller")end;function ENT:Think()BaseClass.Think(self)if self.ToLink then self.ToLinkCounter=(self.ToLinkCounter||0)+1;if self.ToLinkCounter>#self.ToLink then self.ToLink=nil;self.ToLinkCounter=nil else local c=self.ToLink[self.ToLinkCounter]if self:CheckPP(c)then self:LinkEnt(c,true)end end;self:ShowOutput()self:SendMarks()self:NextThink(CurTime()+0.1)return true end end;local d={}function ENT:AddExitPoint()d[self]=true end;local function e(c)if d[c]then d[c]=nil end end;hook.Add("EntityRemoved","WireExitPoint",e)local f=CreateConVar("wire_pod_exit_distance","1000",FCVAR_ARCHIVE,"The maximum distance an exit point can move a player")local function g(h,i)for j,k in pairs(d)do if IsValid(j)&&!j.Position:IsZero()&&j.Entities&&j.Entities[i]then if j.Global then local l=i:GetPos()local m=j.Position-l;local n=m:Length()h:SetPos(l+m/n*math.min(n,math.max(0,f:GetInt()))+Vector(0,0,5))local o=h:EyeAngles()else local p=j.Position:Length()h:SetPos(i:LocalToWorld(j.Position/p*math.min(p,math.max(0,f:GetInt())))+Vector(0,0,5))end;if j.GlobalAngle then h:SetEyeAngles(Angle(j.Angle.p,j.Angle.y,0))else local o=j:LocalToWorldAngles(j.Angle)o.r=0;h:SetEyeAngles(o)end;return end end end;hook.Add("PlayerLeaveVehicle","WireExitPoint",g)function ENT:SendMarks()local q={}for c,k in pairs(self.Entities)do table.insert(q,c)end;WireLib.SendMarks(self,q)end;function ENT:LinkEnt(c,r)c=WireLib.GetClosestRealVehicle(c,nil,!r&&self:GetPlayer())if!IsValid(c)||!c:IsVehicle()then return false,"Must link to a vehicle"end;if self.Entities[c]then return end;self.Entities[c]=true;c:CallOnRemove("ExitPoint.Unlink",function(c)if IsValid(self)then self:UnlinkEnt(c)end end)if!r then self:SendMarks()self:ShowOutput()end;return true end;function ENT:UnlinkEnt(c)if!self.Entities[c]then return end;self.Entities[c]=nil;c:RemoveCallOnRemove("ExitPoint.Unlink")self:SendMarks()self:ShowOutput()return true end;function ENT:ClearEntities()self.Entities={}self:SendMarks()self:ShowOutput()end;function ENT:BuildDupeInfo()local s=BaseClass.BuildDupeInfo(self)||{}if next(self.Entities)then s.marks={}for c,k in pairs(self.Entities)do if IsValid(c)then table.insert(s.marks,c:EntIndex())end end end;return s end;function ENT:ApplyDupeInfo(h,c,s,t)BaseClass.ApplyDupeInfo(self,h,c,s,t)if s.marks then self.ToLink={}for u,v in pairs(s.marks)do self.ToLink[u]=t(v)end end end;duplicator.RegisterEntityClass("gmod_wire_exit_point",WireLib.MakeWireEnt,"Data")
