ENT.Base = "lvs_base_fakehover"

ENT.PrintName = "Barc"
ENT.Author = "Luna"
ENT.Information = "Heracles421"
ENT.Category = "[LVS] - Star Wars"

ENT.Spawnable = true
ENT.AdminSpawnable = false

ENT.MDL = "models/heracles421/galactica_vehicles/barc.mdl"
ENT.GibModels = {
	"models/heracles421/galactica_vehicles/barc.mdl",
}

ENT.AITEAM = 1

ENT.MaxHealth = 2000

ENT.ForceAngleMultiplier = 2
ENT.ForceAngleDampingMultiplier = 1

ENT.ForceLinearMultiplier = 1
ENT.ForceLinearRate = 0.25

ENT.MaxVelocityX = 1100
ENT.MaxVelocityY = 512

ENT.MaxTurnRate = 1.5

ENT.BoostAddVelocityX = 450
ENT.BoostAddVelocityY = 120

ENT.GroundTraceHitWater = true
ENT.GroundTraceLength = 50
ENT.GroundTraceHull = 100

ENT.LAATC_PICKUPABLE = true
ENT.LAATC_DROP_IN_AIR = true
ENT.LAATC_PICKUP_POS = Vector(-260,0,0)
ENT.LAATC_PICKUP_Angle = Angle(0,0,0)

ENT.UIPosition = Vector(250, -135, 0)
ENT.UIAngle = Angle(25, 135, 0)

local COLOR_RED = Color(255,0,0,255)
local COLOR_WHITE = Color(255,255,255,255)

function ENT:OnSetupDataTables()
	self:AddDT( "Bool", "IsCarried" )

	if SERVER then
		self:NetworkVarNotify( "IsCarried", self.OnIsCarried )
	end
end

function ENT:GetAimAngles()
	local trace = self:GetEyeTrace()

	local AimAnglesR = self:WorldToLocalAngles( (trace.HitPos - self:LocalToWorld( Vector(10,-60,81) ) ):GetNormalized():Angle() )
	local AimAnglesL = self:WorldToLocalAngles( (trace.HitPos - self:LocalToWorld( Vector(10,60,81) ) ):GetNormalized():Angle() )

	return AimAnglesR, AimAnglesL
end

function ENT:WeaponsInRange()
	if self:GetIsCarried() then return false end

	local AimAnglesR, AimAnglesL = self:GetAimAngles()
	return not ((AimAnglesR.p >= 20 and AimAnglesL.p >= 20) or (AimAnglesR.p <= -30 and AimAnglesL.p <= -30) or (math.abs(AimAnglesL.y) + math.abs(AimAnglesL.y)) >= 60)
end

function ENT:InitWeapons()
	local weapon = {}

	weapon.Icon = Material 'lvs/weapons/hmg.png'
	weapon.Ammo = 600
	weapon.Delay = 0.1
	weapon.HeatRateUp = 0.15
	weapon.HeatRateDown = 0.5
	weapon.Attack = function( ent )
		if not ent:WeaponsInRange() then return true end
		if (ent:AngleBetweenNormal( ent:GetAimVector(), ent:GetForward() ) > 15) then
			return
		end

		local ID1 = self:LookupAttachment( "front_right_muzzle" )
		local ID2 = self:LookupAttachment( "front_left_muzzle" )
		local ID3 = self:LookupAttachment( "back_right_muzzle" )
		local ID4 = self:LookupAttachment( "back_left_muzzle" )
		local Muzzle1 = self:GetAttachment( ID1 )
		local Muzzle2 = self:GetAttachment( ID2 )
		local Muzzle3 = self:GetAttachment( ID3 )
		local Muzzle4 = self:GetAttachment( ID4 )

		local FirePos = {
			[1] = Muzzle1,
			[2] = Muzzle2,
			[3] = Muzzle3,
			[4] = Muzzle4,
		}

		ent.MirrorPrimary = not ent.MirrorPrimary

		local startpos = ent:LocalToWorld( ent:OBBCenter() )
		local trace = util.TraceHull( {
			start = startpos,
			endpos = (startpos + ent:GetForward() * 50000),
			mins = Vector( -10, -10, -10 ),
			maxs = Vector( 10, 10, 10 ),
			filter = ent:GetCrosshairFilterEnts()
		})

		local bullet = {}
		bullet.Spread = Vector( 0.01,  0.01, 0 )
		bullet.TracerName = "lvs_laser_red_short"
		bullet.Force = 100
		bullet.HullSize = 1
		bullet.Damage = 25
		bullet.Velocity = 12000
		bullet.Attacker = ent:GetDriver()
		bullet.Callback = function(att, tr, dmginfo)
			local effectdata = EffectData()
			effectdata:SetStart( Vector(255,50,50) )
			effectdata:SetOrigin( tr.HitPos )
			effectdata:SetNormal( tr.HitNormal )
			util.Effect( "lvs_laser_impact", effectdata )
		end

		for i = (ent.MirrorPrimary and 1 or 3), (ent.MirrorPrimary and 2 or 4) do
			local fireInfo = FirePos[i]
			local pos = fireInfo.Pos
			local dir = fireInfo.Ang:Up()

			bullet.Src = pos
			bullet.Dir = ent:GetAimVector()

			local effectdata = EffectData()
			effectdata:SetStart( Vector(255,50,50) )
			effectdata:SetOrigin( bullet.Src )
			effectdata:SetNormal( bullet.Dir )
			effectdata:SetEntity( ent )
			util.Effect( "lvs_muzzle_colorable", effectdata )

			ent:LVSFireBullet( bullet )
		end

		ent:TakeAmmo()

		if ent.MirrorPrimary then
			if IsValid(ent.SNDLeft) then
				ent.SNDLeft:PlayOnce()
			end
		else
			if IsValid(ent.SNDRight) then
				ent.SNDRight:PlayOnce()
			end
		end
	end
	weapon.HudPaint = function( ent, X, Y, ply )
		local Col = (ent:AngleBetweenNormal( ent:GetAimVector(), ent:GetForward() ) > 20) and COLOR_RED or COLOR_WHITE
		local Pos2D = ent:GetEyeTrace().HitPos:ToScreen()

		local base = ent:GetVehicle()
		base:PaintCrosshairCenter( Pos2D, Col )
		base:PaintCrosshairOuter( Pos2D, Col )
		base:LVSPaintHitMarker( Pos2D )
	end
	weapon.OnSelect = function( ent )
		ent:EmitSound("physics/metal/weapon_impact_soft3.wav")
	end
	weapon.OnOverheat = function( ent ) ent:EmitSound("lvs/overheat.wav") end

	self:AddWeapon( weapon )
end

ENT.EngineSounds = {
	{
		sound = "heracles421/galactica_vehicles/barc_engine.wav",
		Pitch = 70,
		PitchMin = 0,
		PitchMax = 255,
		PitchMul = 30,
		FadeIn = 0,
		FadeOut = 1,
		FadeSpeed = 1.5,
		UseDoppler = true,
		SoundLevel = 85,
	},
}
--addons/ssrp_lfs/lua/entities/lvs_fighterplane_rotor.lua:
AddCSLuaFile()

ENT.Type            = "anim"

ENT.Spawnable       = false
ENT.AdminSpawnable  = false
ENT.DoNotDuplicate = true

ENT._LVS = true

function ENT:SetupDataTables()
	self:NetworkVar( "Entity",0, "Base" )
end

if SERVER then
	function ENT:Initialize()
		self:SetMoveType( MOVETYPE_NONE )
		self:SetSolid( SOLID_NONE )
		self:DrawShadow( false )
	end

	function ENT:Think()
		return false
	end

	function ENT:UpdateTransmitState()
		return TRANSMIT_ALWAYS
	end

	return
end

ENT._oldActive = false

function ENT:Initialize()
end

function ENT:StopSounds()
	if self._RotorSound1 then
		self._RotorSound1:Stop()
		self._RotorSound1 = nil
	end
	if self._RotorSound2 then
		self._RotorSound2:Stop()
		self._RotorSound2 = nil
	end
end

function ENT:OnActiveChanged( Active )
	if not Active then self:StopSounds() return end

	self:StopSounds()

	self._RotorSound1 = CreateSound( self, "lvs/vehicles/generic/propeller.wav" )
	self._RotorSound1:SetSoundLevel( 70 )
	self._RotorSound1:PlayEx(0,100)

	self._RotorSound2 = CreateSound( self, "lvs/vehicles/generic/propeller_strain.wav" )
	self._RotorSound2:SetSoundLevel( 140 )
	self._RotorSound2:PlayEx(0,100)
end

function ENT:HandleSounds( vehicle, rpm, throttle )

	local rotor_load = vehicle:GetThrustStrenght()

	local mul = math.max( rpm - 470, 0 ) / 330

	local volume = mul * 0.6 + rotor_load * 0.4 * throttle

	local pitch = 80 + mul * 30 - rotor_load * 20 * throttle

	local pitch2 = 100 - rotor_load * 50 * throttle
	local volume2 = math.max(-rotor_load,0) * mul * throttle ^ 2

	if self._RotorSound1 then
		self._RotorSound1:ChangeVolume( volume, 2 )
		self._RotorSound1:ChangePitch( pitch, 0.5 )
	end

	if self._RotorSound2 then
		self._RotorSound2:ChangeVolume( volume2, 0.5 )
		self._RotorSound2:ChangePitch( pitch2, 0 )
	end
end

function ENT:Think()
	local vehicle = self:GetBase()

	if not IsValid( vehicle ) then return end

	local Active = vehicle:GetEngineActive() and LocalPlayer():lvsGetVehicle() == vehicle

	if self._oldActive ~= Active then
		self._oldActive = Active
		self:OnActiveChanged( Active )
	end

	local Throttle = vehicle:GetThrottle()

	local TargetRPM = vehicle:GetEngineActive() and (300 + Throttle * 500) or 0

	vehicle.RotorRPM = vehicle.RotorRPM and vehicle.RotorRPM + (TargetRPM - vehicle.RotorRPM) * RealFrameTime() or 0

	if Active then
		self:HandleSounds( vehicle, vehicle.RotorRPM, Throttle )
	end
end

function ENT:OnRemove()
	self:StopSounds()
end

function ENT:Draw()
end

function ENT:DrawTranslucent()
end

