AddCSLuaFile()

ENT.Type = "anim"
ENT.Base = 'base_srp'

ENT.PrintName = "Maintenance Station"
ENT.Author = "Luna"
ENT.Information = "Repairs Vehicles"
ENT.Category = "[LVS]"

ENT.Spawnable = true
ENT.AdminOnly = false
ENT.NetworkPlayerUse = true
ENT.RepairStation = true

ENT.FoundVehicles = {}
ENT.MinMax = {
	Vector(0, -256, 0),
	Vector(512, 256, 150),
}

function ENT:SetupDataTables()
	self:NetworkVar('Bool', 0, 'Parked')
	self:NetworkVar('Bool', 1, 'GivesAmmo')
	self:NetworkVar('Bool', 2, 'GivesHealth')
	self:NetworkVar('Bool', 3, 'Enabled')
end

if SERVER then
	util.AddNetworkString 'lvs.ChangeRepairStation'

	function ENT:SpawnFunction( ply, tr, ClassName )
		if not tr.Hit then return end

		local ent = ents.Create( ClassName )
		ent:SetPos( tr.HitPos + tr.HitNormal )
		ent:Spawn()
		ent:Activate()

		return ent
	end

	function ENT:OnTakeDamage( dmginfo )
	end

	function ENT:Initialize()
		self:SetModel( "models/starwars/syphadias/props/sw_tor/bioware_ea/props/coruscant/cor_works_console_01.mdl" )
		self:SetTrigger(true)
		self:DrawShadow(false)
		self:SetSolid(SOLID_BBOX)

		local zone = srp.buffs.CreateAura(self, Vector(250, 0, 0), self:GetAngles(), 'models/hunter/blocks/cube4x4x2.mdl', 2)
		zone.CanBuff = function(s, ent)
			return ent.LVS
		end

		zone.OnZoneEntered = function(s, entity)
			s.Parent:Refil(entity)
		end

		zone.OnZoneExit = function(s, entity)
			s.Parent:Refil( entity )
			s.Parent:SetParked(false)
		end
	end

	function ENT:Refil( entity )
		if not self:GetEnabled() then return end
		if not IsValid( entity ) or self:GetParked() then return end

		if self:GetGivesHealth() and entity:GetHP() ~= entity:GetMaxHP() then
			entity:SetHP( entity:GetMaxHP() )
			entity:EmitSound("npc/dog/dog_servo2.wav")
		end

		if self:GetGivesAmmo() and entity:WeaponRestoreAmmo() then
			entity:EmitSound("items/ammo_pickup.wav")
		end

		entity:OnMaintenance()
		self:SetParked(true)
	end

	function ENT:StartTouch( entity )
		if not entity.LVS then
			return
		end

		self:Refil( entity )
	end

	function ENT:EndTouch( entity )
		if not entity.LVS then
			return
		end

		self:Refil( entity )
		self:SetParked(false)
	end

	function ENT:Touch( entity )
	end

	function ENT:Think()
		return false
	end

	net.Receive('lvs.ChangeRepairStation', function(_, pl)
		if not pl:IsTrialGamemaster() then return end

		local ent = net.ReadEntity()
		if not ent.RepairStation then
			return
		end

		local enabled = net.ReadBool()
		local ammo = net.ReadBool()
		local health = net.ReadBool()

		ent:SetEnabled(enabled)
		ent:SetGivesAmmo(ammo)
		ent:SetGivesHealth(health)
	end)
end

if CLIENT then
	local WhiteList = {
		["weapon_physgun"] = true,
		["weapon_physcannon"] = true,
		["gmod_tool"] = true,
	}

	local mat = Material( "models/wireframe" )
	local FrameMat = Material( "lvs/3d2dmats/frame.png" )
	local AmmoMat = Material( "lvs/weapons/bullet.png" )
	local HealthMat = Material ( "galactic/ui/talents/health.png" )
	local maxDistance = 2000 ^ 2
	local orange = Color(255, 150, 0)
	local green = Color(100, 200, 100)
	local red = Color(100, 25, 25)
	local up = Angle(0,90,0)
	local anchor = -512
	local anchory = 512

	function ENT:Initialize()
		self:SetRenderBounds(self.MinMax[1], self.MinMax[2])
	end

	function ENT:Draw()
		self:DrawModel()

		local ply = LocalPlayer()
		local Pos = self:GetPos()
		local currentDistance = Pos:DistToSqr(ply:GetPos())
		if currentDistance > maxDistance then
			return
		end

		local percentDistance = 1 - (currentDistance / maxDistance)
		local alpha = (IsValid(ply:GetVehicle()) and 150 or 55) * percentDistance
		local parked = self:GetEnabled() and (self:GetParked() and green or orange) or red

		parked.a = alpha

		local givesAmmo = self:GetGivesAmmo()
		local givesHealth = self:GetGivesHealth()
		local count = (givesAmmo and 1 or 0) + (givesHealth and 1 or 0)
		local offset = 256 * (count-1)

		cam.Start3D2D( self:LocalToWorld( Vector(0,0, self:OBBMins().z + 2 ) ), self:LocalToWorldAngles( up ), 0.25 )
			surface.SetDrawColor( parked )

			surface.SetMaterial( FrameMat )
			surface.DrawTexturedRect( -1024, 0, 2048, 2048 )

			if givesAmmo then
				surface.SetMaterial( AmmoMat )
				surface.DrawTexturedRect( anchor, anchory + offset, 1024, 1024 )
			end

			if givesHealth then
				surface.SetMaterial( HealthMat )
				surface.DrawTexturedRect( anchor + 256, anchory - offset + 256, 512, 512 )
			end
		cam.End3D2D()
	end

	function ENT:OnRemove()
		if IsValid( self._RepairMDL ) then
			self._RepairMDL:Remove()
		end
	end

	function ENT:ClientPlayerUse()
		if not LocalPlayer():IsTrialGamemaster() then
			return
		end

		if IsValid(self.UI) then
			self.UI:Remove()
		end

		srp.ui.Create('srp_window', function(win)
			win:SetSize(400, 290)
			win:SetTitle 'Repair Station Settings'
			win:SetBackgroundColor(col.grey_light)
			win:Center()
			win:MakePopup()
			win:DockPadding(25, 50, 25, 25)
			win:AddPanel('srp_label', function(pnl)
				pnl:SetText 'Station Status'
				pnl:SetFont 'ui_label.l'
				pnl:Dock(TOP)
			end)
			win:AddPanel('srp_checkbox', function(pnl)
				pnl:Dock(TOP)
				pnl:SetWide(150)
				pnl:DockPadding(2, 2, 0, 2)
				pnl:SetOptions(
					{
						name = 'Disabled',
						background = col.red,
						gradient = col.red_dark
					},
					{
						name = 'Enabled',
						background = col.green,
						gradient = col.green_dark
					}
				)

				pnl:SetPosition(self:GetEnabled() and 2 or 1)
				win.Enabled = pnl
			end)
			win:AddPanel('srp_label', function(pnl)
				pnl:SetText 'Give Ammo'
				pnl:SetFont 'ui_label.l'
				pnl:Dock(TOP)
				pnl:DockMargin(5, 10, 5, 5)
			end)
			win:AddPanel('srp_checkbox', function(pnl)
				pnl:Dock(TOP)
				pnl:SetWide(150)
				pnl:DockPadding(2, 2, 0, 2)
				pnl:SetOptions(
					{
						name = 'Disabled',
						background = col.red,
						gradient = col.red_dark
					},
					{
						name = 'Enabled',
						background = col.green,
						gradient = col.green_dark
					}
				)

				pnl:SetPosition(self:GetGivesAmmo() and 2 or 1)
				win.Ammo = pnl
			end)
			win:AddPanel('srp_label', function(pnl)
				pnl:SetText 'Give Health'
				pnl:SetFont 'ui_label.l'
				pnl:Dock(TOP)
				pnl:DockMargin(5, 10, 5, 5)
			end)
			win:AddPanel('srp_checkbox', function(pnl)
				pnl:Dock(TOP)
				pnl:SetWide(150)
				pnl:DockPadding(2, 2, 0, 2)
				pnl:SetOptions(
					{
						name = 'Disabled',
						background = col.red,
						gradient = col.red_dark
					},
					{
						name = 'Enabled',
						background = col.green,
						gradient = col.green_dark
					}
				)

				pnl:SetPosition(self:GetGivesHealth() and 2 or 1)
				win.Health = pnl
			end)

			win:AddPanel('srp_button_rounded', function(btn)
				btn:SetText 'Submit'
				btn:Dock(BOTTOM)
				btn:DockMargin(0, 0, 0, 0)

				btn:LeftClick(function()
					net.Start 'lvs.ChangeRepairStation'
						net.WriteEntity(self)
						net.WriteBool(win.Enabled:GetPosition() == 2)
						net.WriteBool(win.Ammo:GetPosition() == 2)
						net.WriteBool(win.Health:GetPosition() == 2)
					net.SendToServer()
				end)
			end)

			self.UI = win
		end)
	end
end
