        close:Remove()
    end

    local rewards = vgui.Create("SFrame", close)
    :SetSize(slib.getScaledSize(300, "x"),slib.getScaledSize(200, "y"))
    :setTitle(slib.getLang("sreward", sReward.config["language"], "rewards_title", data.name))
