    end},
    [4] = {id = "tokens", instructions = {["player"] = true, ["int"] = true, ["plydata"] = true}},
    [5] = {id = "coupons", instructions = {["jsontable"] = true}, func = function(data)
        for k,v in pairs(sReward.data["coupons"]) do
            if !istable(v) then continue end
