		createStatus(false, language.GetPhrase("svmod.home.addon_not_up_to_date") .. " (" .. SVMOD.FCFG.Version .. " - " .. SVMOD.FCFG.LastVersion .. ")")
	end

	if SVMOD.Data and #data.LastVehicleUpdate > 0 then
