        ranks[rank.id] = rank
    end

    VoidFactions.PrintDebug("Received " .. length .. " rank templates")
    VoidFactions.RankTemplates = ranks

    hook.Run("VoidFactions.Rank.RankTemplatesReceived", ranks)
end)
