    YAWS.Core.LogDebug("(", message, ") Payload Size: ", YAWS.Core.LogColors.TextHighlightColor, string.NiceSize(len / 8), color_white, " / ", YAWS.Core.LogColors.TextHighlightColor, (len / 8), " bytes")

