    if(YAWS.ManualConfig.SupressAsyncTraces) then
        if(ply) then 
            YAWS.Core.Message(ply, "error")
        end 

        return
    end 

    YAWS.Core.LogError("Error in handling action ", YAWS.Core.LogColors.TextHighlightColor, action, color_white, ": ", err)

    if(ply) then 
        YAWS.Core.Message(ply, "error")
