function OnePrint:Play2DSound( sFileName )
    if CSoundSource then
        CSoundSource:Stop()
        CSoundSource = false
    end

    CSoundSource = CreateSound( LocalPlayer(), sFileName )
    CSoundSource:PlayEx( 50, 100 )
end

